"use client";

import { useBlockNumber } from "@/registry/polkadot-ui/blocks/block-number/hooks/use-block-number";
import { usePolkadot } from "@/registry/polkadot-ui/providers/polkadot-provider";
import { Button } from "@/registry/polkadot-ui/ui/button";

export function BlockNumber() {
  const { blockNumber, error } = useBlockNumber();
  const {
    setApi,
    availableChains,
    isConnected,
    isLoading: isLoading,
    currentChain,
    chainName,
  } = usePolkadot();

  if (isLoading(currentChain)) {
    return (
      <div className="w-full max-w-md p-4 border border-gray-200 rounded-md bg-white">
        <div className="mb-4">
          <h3 className="text-lg font-semibold">Block Number</h3>
          <p className="text-sm text-gray-600">Loading...</p>
        </div>
        <div className="flex items-center space-x-2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600"></div>
          <span className="text-sm text-gray-600">
            Connecting to {chainName}...
          </span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="w-full max-w-md p-4 border border-red-200 rounded-md bg-red-50">
        <div className="mb-4">
          <h3 className="text-lg font-semibold text-red-600">Block Number</h3>
          <p className="text-sm text-red-500">Error loading block number</p>
        </div>
        <div className="text-red-600 text-sm">{error}</div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-md p-4 border border-gray-200 rounded-md bg-white">
      <div className="mb-4">
        <h3 className="text-lg font-semibold">Block Number</h3>
        <p className="text-sm text-gray-600">Current block on {chainName}</p>
      </div>
      <div className="space-y-4">
        <div className="text-3xl font-bold text-blue-600">
          {blockNumber?.toLocaleString() || "Loading..."}
        </div>

        <div className="space-y-2">
          <div className="text-sm text-gray-600">
            Current chain: <span className="font-mono">{chainName}</span>
          </div>

          <div className="flex gap-2 flex-wrap">
            {availableChains.map((chainId) => (
              <Button
                key={chainId}
                variant={chainId === currentChain ? "default" : "outline"}
                size="sm"
                onClick={() => setApi(chainId)}
                disabled={isLoading(chainId)}
              >
                {chainId}
                {isConnected(chainId) && (
                  <span className="ml-1 text-xs text-green-600">‚óè</span>
                )}
              </Button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

"use client";
import { usePolkadot } from "@/registry/polkadot-ui/providers/polkadot-provider";
import { useEffect, useState } from "react";

export function useBlockNumber() {
  // Use the new provider API without specifying a chain - it uses the currently active chain
  const { api, isLoading, error: apiError, currentChain } = usePolkadot();
  const [blockNumber, setBlockNumber] = useState<number | null>(null);

  useEffect(() => {
    if (!api || isLoading(currentChain)) return;

    // No type assertions needed - TypeScript knows the exact API structure!
    const subscription = api.query.System.Number.watchValue("best").subscribe(
      (value: number) => {
        setBlockNumber(value);
      }
    );

    return () => {
      subscription?.unsubscribe();
    };
  }, [api, isLoading, currentChain]);

  return {
    blockNumber,
    isLoading,
    error: apiError,
  };
}

"use client";

import { createClient, TypedApi } from "polkadot-api";
import { getWsProvider } from "polkadot-api/ws-provider/web";
import { withPolkadotSdkCompat } from "polkadot-api/polkadot-sdk-compat";
import { createContext, useContext, useEffect, useState } from "react";
import {
  polkadotConfig,
  type ChainId,
  type ChainDescriptor,
} from "@/registry/polkadot-ui/lib/config.polkadot-ui";
import {
  getChainIds,
  getChainConfig,
  isValidChainId,
} from "@/registry/polkadot-ui/lib/utils.polkadot-ui";

// Type for the API based on configured chains
type ConfiguredChainApi<T extends ChainId> = TypedApi<ChainDescriptor<T>>;

// Create a composite API type that includes all registered chains
type CompositeApi = {
  [K in ChainId]: ConfiguredChainApi<K>;
};

interface PolkadotContextValue {
  // Current active chain and its API
  currentChain: ChainId;
  api: ConfiguredChainApi<ChainId> | null;
  isLoading: (chainId: ChainId) => boolean;
  error: string | null;

  // All APIs for all registered chainsp
  apis: Partial<CompositeApi>;

  // Function to switch active chain (type-safe)
  setApi: (chainId: ChainId) => void;

  // Connection management
  disconnect: () => void;
  isConnected: (chainId: ChainId) => boolean;

  // Chain information
  chainName: string | null;
  availableChains: ChainId[];
}

const PolkadotContext = createContext<PolkadotContextValue | undefined>(
  undefined
);

interface PolkadotProviderProps {
  children: React.ReactNode;
}

export function PolkadotProvider({ children }: PolkadotProviderProps) {
  const [currentChain, setCurrentChain] = useState<ChainId>(
    polkadotConfig.defaultChain
  );
  const [apis, setApis] = useState<Partial<CompositeApi>>({});
  const [clients, setClients] = useState<
    Map<ChainId, ReturnType<typeof createClient>>
  >(new Map());
  const [loadingStates, setLoadingStates] = useState<Map<ChainId, boolean>>(
    new Map()
  );
  const [errorStates, setErrorStates] = useState<Map<ChainId, string | null>>(
    new Map()
  );

  // Initialize the default chain on mount
  useEffect(() => {
    initializeChain(polkadotConfig.defaultChain);
  }, []);

  const initializeChain = async (chainId: ChainId) => {
    // Don't initialize if already connected
    if (apis[chainId]) return;

    setLoadingStates((prev) => new Map(prev).set(chainId, true));
    setErrorStates((prev) => new Map(prev).set(chainId, null));

    try {
      const chainConfig = getChainConfig(polkadotConfig.chains, chainId);

      console.log(
        `Connecting to ${chainConfig.displayName} at ${chainConfig.endpoint}`
      );

      // Create client with the selected chain
      const client = createClient(
        withPolkadotSdkCompat(getWsProvider(chainConfig.endpoint))
      );

      // Get typed API for the selected chain
      const typedApi = client.getTypedApi(
        chainConfig.descriptor
      ) as ConfiguredChainApi<typeof chainId>;

      setClients((prev) => new Map(prev).set(chainId, client));
      setApis((prev) => ({ ...prev, [chainId]: typedApi }));

      console.log(`Successfully connected to ${chainConfig.displayName}`);
    } catch (err) {
      console.error(`Failed to initialize ${chainId}:`, err);
      setErrorStates((prev) =>
        new Map(prev).set(
          chainId,
          err instanceof Error
            ? err.message
            : "Failed to initialize Polkadot API"
        )
      );
    } finally {
      setLoadingStates((prev) => new Map(prev).set(chainId, false));
    }
  };

  const setApi = (chainId: ChainId) => {
    if (!isValidChainId(polkadotConfig.chains, chainId)) {
      console.error(`Invalid chain ID: ${chainId}`);
      return;
    }

    setCurrentChain(chainId);
    // Initialize the chain if not already connected
    if (!apis[chainId]) {
      initializeChain(chainId);
    }
  };

  const disconnect = () => {
    clients.forEach((client) => client.destroy());
    setClients(new Map());
    setApis({});
    setLoadingStates(new Map());
    setErrorStates(new Map());
    setCurrentChain(polkadotConfig.defaultChain);
  };

  const isConnected = (chainId: ChainId): boolean => {
    return !!apis[chainId];
  };

  const isLoading = (chainId: ChainId): boolean => {
    return loadingStates.get(chainId) || false;
  };

  const currentChainConfig = getChainConfig(
    polkadotConfig.chains,
    currentChain
  );

  const value: PolkadotContextValue = {
    currentChain,
    api: apis[currentChain] || null,
    error: errorStates.get(currentChain) || null,
    apis,
    setApi,
    disconnect,
    isConnected,
    isLoading,
    chainName: currentChainConfig.displayName,
    availableChains: getChainIds(polkadotConfig.chains),
  };

  return (
    <PolkadotContext.Provider value={value}>
      {children}
    </PolkadotContext.Provider>
  );
}

export function usePolkadot(): PolkadotContextValue {
  const context = useContext(PolkadotContext);

  if (context === undefined) {
    throw new Error("usePolkadot must be used within a PolkadotProvider");
  }

  return context;
}

// Helper to get properly typed API (maintains backward compatibility)
export function useTypedPolkadotApi(): ConfiguredChainApi<ChainId> | null {
  const { api } = usePolkadot();
  return api;
}

// Type exports
export type { ChainId, ConfiguredChainApi, CompositeApi };

import type { ChainConfig } from "@/registry/polkadot-ui/lib/types.polkadot-ui";

// Generic helper functions that work with any polkadot config
export function getChainIds<T extends Record<string, ChainConfig>>(
  chains: T
): (keyof T)[] {
  return Object.keys(chains) as (keyof T)[];
}

export function getChainConfig<
  T extends Record<string, ChainConfig>,
  K extends keyof T
>(chains: T, chainId: K): T[K] {
  return chains[chainId];
}

export function isValidChainId<T extends Record<string, ChainConfig>>(
  chains: T,
  chainId: string
): chainId is string & keyof T {
  return chainId in chains;
}

import type { ChainDefinition } from "polkadot-api";

export interface ChainConfig {
  readonly descriptor: ChainDefinition;
  readonly endpoint: string;
  readonly displayName: string;
}

export interface PolkadotConfig<
  TChains extends Readonly<Record<string, ChainConfig>> = Readonly<
    Record<string, ChainConfig>
  >
> {
  readonly chains: TChains;
  readonly defaultChain: keyof TChains;
}

export function definePolkadotConfig<
  const TChains extends Readonly<Record<string, ChainConfig>>
>(config: PolkadotConfig<TChains>) {
  return config;
}

// To add more chains, run: npx papi add <chain-name> -n <chain-name>
// Then import the descriptor here and add it to the chains configuration
import { paseo_asset_hub, paseo } from "@polkadot-api/descriptors";
import { definePolkadotConfig } from "@/registry/polkadot-ui/lib/types.polkadot-ui";

export const polkadotConfig = definePolkadotConfig({
  chains: {
    paseo_asset_hub: {
      descriptor: paseo_asset_hub,
      endpoint: "wss://sys.ibp.network/asset-hub-paseo",
      displayName: "Paseo Asset Hub",
    },
    paseo: {
      descriptor: paseo,
      endpoint: "wss://sys.ibp.network/paseo",
      displayName: "Paseo Relay Chain",
    },
    // Add more chains here after running `npx papi add <chain-name>`
    // Example for adding Polkadot mainnet:
    // 1. Run: npx papi add polkadot -n polkadot
    // 2. Import: import { polkadot } from "@polkadot-api/descriptors";
    // 3. Add configuration:
    // polkadot: {
    //   descriptor: polkadot,
    //   endpoint: "wss://polkadot-rpc.publicnode.com",
    //   displayName: "Polkadot",
    // },
  },
  defaultChain: "paseo_asset_hub",
} as const);

// Simple type aliases for type safety
export type ChainId = keyof typeof polkadotConfig.chains;
export type ChainDescriptor<T extends ChainId> =
  (typeof polkadotConfig.chains)[T]["descriptor"];
